/**
 * @enum
 * @type {{BACKSPACE : string, FORM_FEED : string, NEWLINE : string, CARRIAGE_RETURN : string, HORIZONTAL_TABULATOR : string,
 *    VERTICAL_TABULATOR : string}}
 */
Bee.String = Bee.String || {};
Bee.String.Escape = {
   BACKSPACE            : '\b',
   FORM_FEED            : '\f',
   NEWLINE              : '\n',
   CARRIAGE_RETURN      : '\r',
   HORIZONTAL_TABULATOR : '\t',
   VERTICAL_TABULATOR   : '\v',
   SINGLE_QUOTE         : '\'',
   DOUBLE_QUOTE         : '\"',
   BACKSLASH            : '\\',
};

